mapper.insert:
			INSERT INTO TB_LOGIN_HIST(SESSION_ID
									, USER_ID
			                        , LOGIN_DT			                        
			                        , USER_TY_CODE			                        
			                        , CONECT_IP)
			VALUES (#{sessionId}
			      , #{userId}
			      , NOW()
			      , #{userTyCode}
			      , #{conectIp}
			      )

mapper.update:
UPDATE TB_LOGIN_HIST
   SET LOGOUT_DT = NOW()
     , LOGOUT_STTUS_CODE = #{logoutSttusCode}
WHERE SESSION_ID= #{sessionId}
